import React, { useState } from 'react';

const Home = () => {
  const [hoveredSide, setHoveredSide] = useState(null);

  const handleMouseEnter = (side) => {
    setHoveredSide(side);
  };

  const handleMouseLeave = () => {
    setHoveredSide(null);
  };

  return (
    <div className="container bg-gray-900 h-screen flex">
      <div className={`split left relative w-1/2 bg-cover bg-center ${hoveredSide === 'left' ? 'hover-left' : ''}`} style={{ backgroundImage: "url('img/designer.jpg')" }}
        onMouseEnter={() => handleMouseEnter('left')}
        onMouseLeave={handleMouseLeave}
      >
        <div className="absolute inset-0 bg-red-700 bg-opacity-70 transition-all duration-1000 opacity-0 hover:opacity-100">
          <h1 className="text-white text-4xl absolute top-1/4 left-1/2 transform -translate-x-1/2">The Designer</h1>
          <a href="#" className="button absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-transparent border-2 border-white text-white py-2.5 px-6 text-base font-bold uppercase hover:bg-red-700 hover:border-red-700">Read More</a>
        </div>
      </div>
      <div className={`split right relative w-1/2 bg-cover bg-center ${hoveredSide === 'right' ? 'hover-right' : ''}`} style={{ backgroundImage: "url('img/programmer.png')" }}
        onMouseEnter={() => handleMouseEnter('right')}
        onMouseLeave={handleMouseLeave}
      >
        <div className="absolute inset-0 bg-gray-900 bg-opacity-80 transition-all duration-1000 opacity-0 hover:opacity-100">
          <h1 className="text-white text-4xl absolute top-1/4 left-1/2 transform -translate-x-1/2">The Programmer</h1>
          <a href="#" className="button absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-transparent border-2 border-white text-white py-2.5 px-6 text-base font-bold uppercase hover:bg-gray-900 hover:border-gray-900">Read More</a>
        </div>
      </div>
    </div>
  );
}

export default Home;
