import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

function Login() {
    const [message, setMessage] = useState(null); // State to hold login message

    const formik = useFormik({
        initialValues: {
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            email: Yup.string().email('Invalid email').required('Email is required'),
            password: Yup.string().required('Password is required')
        }),
        onSubmit: async (values) => {
            try {
                const response = await axios.post('/login', values);
                if (response.status === 200) {
                    setMessage('Logged in successfully');
                    setTimeout(() => {
                        setMessage(null); // Clear success message after 3 seconds
                    }, 3000);
                    formik.resetForm(); // Reset form after successful login
                }
            } catch (error) {
                console.error('Error logging in:', error);
                setMessage(error.response?.data?.message || 'Invalid credentials. Please try again.');
                setTimeout(() => {
                    setMessage(null); // Clear error message after 3 seconds
                }, 3000);
            }
        }
    });

    return (
        <div className='m-5'>
            <form className='text-blue-600' onSubmit={formik.handleSubmit}>
                <h2 className='text-xl'>Login</h2>
                {message && <p className={message === 'Logged in successfully' ? 'text-green-500 mb-4' : 'text-red-500 mb-4'}>{message}</p>} {/* Display login message */}
                <div>
                    <input 
                        className='rounded-md border border-blue-300 focus:outline-none mt-6'
                        type="email"
                        name='email'
                        placeholder='Enter your email'
                        onChange={formik.handleChange}
                        value={formik.values.email}
                    />
                    {formik.touched.email && formik.errors.email ? (
                        <p className='text-red-500'>{formik.errors.email}</p>
                    ) : null}
                </div>
                <div>
                    <input
                        className='rounded-md border border-blue-300 focus:outline-none mt-6'
                        type="password"
                        name='password'
                        placeholder='Enter your password'
                        onChange={formik.handleChange}
                        value={formik.values.password}
                    />
                    {formik.touched.password && formik.errors.password ? (
                        <p className='text-red-500'>{formik.errors.password}</p>
                    ) : null}
                </div>
                <div className='ml-8 mt-3'>
                    <button type='submit' className='border border-accent1 p-3 rounded'>
                        Login
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Login;
