import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Signup() {
    const [message, setMessage] = useState(null);

    const formik = useFormik({
        initialValues: {
            name:'',
            email:'',
            password:''
        },
        validationSchema: Yup.object({
            name: Yup.string().max(20, "Must be 20 characters or less").required("Name is required"),
            email: Yup.string().email('Invalid email').required('Email is required'),
            password: Yup.string().required('Password is required')
        }),
        onSubmit: async(values) => {
            try {
                const response = await axios.post('/signup', values);
                if (response.status === 201) {
                    setMessage('User created successfully');
                    formik.resetForm();
                    setTimeout(() => {
                        setMessage(null);
                    }, 3000);
                }
            } catch (error) {
                console.error('Error signing up:', error);
            }
        }

    });

    return (
        <div className='flex items-center justify-center min-h-screen bg-gray-100'>
            <div className='bg-white shadow-md rounded-lg p-8 w-full sm:w-3/4 md:w-1/2 lg:w-1/3'>
                <h2 className='text-3xl text-center font-bold mb-6 text-blue-600'>Signup</h2>
                <form onSubmit={formik.handleSubmit}>
                    <div className='mb-4'>
                        <label htmlFor='name' className='block text-sm font-medium text-gray-700'>
                            Name
                        </label>
                        <input 
                            id='name'
                            name='name'
                            type='text'
                            className='mt-1 block w-full border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm'
                            placeholder='Enter your name'
                            onChange={formik.handleChange}
                            value={formik.values.name}
                        />
                        {formik.touched.name && formik.errors.name && <p className='text-red-500 text-sm mt-1'>{formik.errors.name}</p>}
                    </div>
                    <div className='mb-4'>
                        <label htmlFor='email' className='block text-sm font-medium text-gray-700'>
                            Email
                        </label>
                        <input 
                            id='email'
                            name='email'
                            type='email'
                            className='mt-1 block w-full border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm'
                            placeholder='Enter your email'
                            onChange={formik.handleChange}
                            value={formik.values.email}
                        />
                        {formik.touched.email && formik.errors.email && <p className='text-red-500 text-sm mt-1'>{formik.errors.email}</p>}
                    </div>
                    <div className='mb-6'>
                        <label htmlFor='password' className='block text-sm font-medium text-gray-700'>
                            Password
                        </label>
                        <input 
                            id='password'
                            name='password'
                            type='password'
                            className='mt-1 block w-full border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm'
                            placeholder='Enter your password'
                            onChange={formik.handleChange}
                            value={formik.values.password}
                        />
                        {formik.touched.password && formik.errors.password && <p className='text-red-500 text-sm mt-1'>{formik.errors.password}</p>}
                    </div>
                    <div className='flex items-center justify-center'>
                        <button
                            type='submit'
                            className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50'
                        >
                            Sign Up
                        </button>
                    </div>
                </form>
                {message && <p className='text-green-500 text-sm mt-4'>{message}</p>}
                <p className='text-gray-600 text-sm mt-4'>
                    Already have an account? <Link to='/login' className='text-blue-500'>Login</Link> 
                </p>
            </div>
        </div>
    );
}

export default Signup;
