import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function Login() {
    const [message, setMessage] = useState('');
    const history = useHistory();

    const handleSubmit = async (event) => {
        event.preventDefault();
        const form = event.target;
        const formData = new FormData(form);
        const email = formData.get('email');
        const password = formData.get('password');
        console.log('Form data:', { email, password });

        try {
            const response = await axios.post('http://localhost:5000/login', { email, password });
            const { access_token, role } = response.data;
            localStorage.setItem('token', access_token);
            localStorage.setItem('role', role); // Store user role
            setMessage('Logged in successfully.');
            // Redirect based on user role or to events page
            if (role === 'admin') {
                history.push('/admin-dashboard'); // Example admin dashboard route
            } else {
                history.push('/events'); // Redirect to events page for regular users
            }
        } catch (error) {
            console.error('Error logging in:', error);
            setMessage('Invalid credentials. Please try again.');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" required />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" required />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
