import React, { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Signup() {
    const [message, setMessage] = useState(null);

    const formik = useFormik({
        initialValues: {
            name:'',
            email:'',
            password:''
        },
        validationSchema: Yup.object({
            name: Yup.string().max(20, "Must be 20 characters or less").required("Name is required"),
            email: Yup.string().email('Invalid email').required('Email is required'),
            password: Yup.string().required('Password is required')
        }),
        onSubmit: async(values) => {
            try{
                const response = await axios.post('/signup', values);
                if (response.status === 201) {
                    setMessage('User created successfully');
                    formik.resetForm();
                    setTimeout(()=>{
                        setMessage(null);
                    }, 3000)
                }
            } catch (error) {
                console.error('Error signing up:', error)
            }
        }

    });

    return (
        <div className='flex flex-col items-center justify-center min-h-screen bg-blue-100'>
            <form className='bg-white shadow-md rounded px-10 pt-8 pb-12 mb-4 w-full max-w-lg' onSubmit={formik.handleSubmit}>
                <h2 className='text-3xl text-center font-bold mb-8'>Signup</h2>
                <div className='mb-6'>
                    <input 
                        className='shadow appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                        type="text"
                        name='name'
                        placeholder='Enter your name'
                        onChange={formik.handleChange}
                        value={formik.values.name}
                    />
                    {formik.touched.name && formik.errors.name && <p className='text-red-500 mt-2'>{formik.errors.name}</p>}
                </div>
                <div className='mb-6'>
                    <input 
                        className='shadow appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                        type="email"
                        name='email'
                        placeholder='Enter your email'
                        onChange={formik.handleChange}
                        value={formik.values.email}
                    />
                    {formik.touched.email && formik.errors.email && <p className='text-red-500 mt-2'>{formik.errors.email}</p>}
                </div>
                <div className='mb-6'>
                    <input
                        className='shadow appearance-none border border-gray-300 rounded w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:shadow-outline'
                        type="password"
                        name='password'
                        placeholder='Enter your password'
                        onChange={formik.handleChange}
                        value={formik.values.password}
                    />
                    {formik.touched.password && formik.errors.password && <p className='text-red-500 mt-2'>{formik.errors.password}</p>}
                </div>
                <div className='flex items-center justify-center'>
                    <button
                        type='submit'
                        className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded focus:outline-none focus:shadow-outline'
                    >
                        Sign Up
                    </button>
                </div>
                {message && (
                    <p className='text-green-500 text-center mt-4'>{message}</p>
                )}
            </form>
            <p className='text-gray-600 text-lg'>
                Already have an account? <Link to='/login' className='text-blue-500'>Login</Link>
            </p>
        </div>
    )
}

export default Signup;
