// EventManagement.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventManagement = () => {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    date: '',
    location: '',
    created_by: 1 // Example: Replace with dynamic admin user ID
  });

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Fetch events error:', error);
      // Handle error (show error message, etc.)
    }
  };

  const handleCreateEvent = async () => {
    try {
      const response = await axios.post('http://localhost:5000/events', formData);
      console.log(response.data);
      // Handle success (update state, show success message, etc.)
      fetchEvents(); // Refresh events after creation
    } catch (error) {
      console.error('Create event error:', error);
      // Handle error (show error message, etc.)
    }
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/events/${eventId}`);
      console.log(response.data);
      // Handle success (update state, show success message, etc.)
      fetchEvents(); // Refresh events after deletion
    } catch (error) {
      console.error('Delete event error:', error);
      // Handle error (show error message, etc.)
    }
  };

  return (
    <div>
      <h2>Event Management</h2>
      <h3>Create Event</h3>
      <form onSubmit={handleCreateEvent}>
        <input type="text" name="title" placeholder="Title" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} required />
        <input type="text" name="description" placeholder="Description" value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} required />
        <input type="date" name="date" value={formData.date} onChange={(e) => setFormData({ ...formData, date: e.target.value })} required />
        <input type="text" name="location" placeholder="Location" value={formData.location} onChange={(e) => setFormData({ ...formData, location: e.target.value })} required />
        <button type="submit">Create Event</button>
      </form>

      <h3>Events List</h3>
      <ul>
        {events.map(event => (
          <li key={event.id}>
            <div>Title: {event.title}</div>
            <div>Description: {event.description}</div>
            <div>Date: {event.date}</div>
            <div>Location: {event.location}</div>
            <button onClick={() => handleDeleteEvent(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventManagement;
