import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function EventManagement() {
  const [events, setEvents] = useState([]);
  const [message, setMessage] = useState(null);
  const [messageEventId, setMessageEventId] = useState(null);
  const navigate = useNavigate();

  const isAuthenticated = !!localStorage.getItem('token');

  useEffect(() => {
    async function fetchEvents() {
      try {
        const response = await axios.get('/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    }
    fetchEvents();
  }, []);

  const handleRegister = async (eventId) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      const token = localStorage.getItem('token');

      const response = await axios.post(
        `/events/register/${eventId}`,
        null,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data.message === 'Already registered for the event') {
        setMessage("You have already registered for this event.");
        setTimeout(() => {
          setMessage(null);
          setMessageEventId(null);
        }, 1000);
      } else {
        setMessage('Registered for the event!');
        setMessageEventId(eventId);
        setTimeout(() => {
          setMessage(null);
          setMessageEventId(null);
        }, 2000);

        const updatedEvents = events.map((event) =>
          event.id === eventId ? { ...event, registered: true } : event
        );
        setEvents(updatedEvents);
      }
    } catch (error) {
      console.error('Error registering for event:', error);

      if (error.response) {
        console.log('Response data:', error.response.data);
        if (error.response.data.message === 'Already registered for the event') {
          setMessage('You have already registered for this event.');
          setMessageEventId(eventId);
          setTimeout(() => {
            setMessage(null);
            setMessageEventId(null);
          }, 2000);
        } else {
          setMessage('An error occurred. Please try again.');
          setMessageEventId(eventId);
          setTimeout(() => {
            setMessage(null);
            setMessageEventId(null);
          }, 1000);
        }
      }
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Upcoming Events</h2>
      <div className="max-w-3xl w-full">
        {events.map((event) => (
          <div key={event.id} className="bg-indigo-200 shadow-md rounded-lg px-8 py-6 mb-4">
            {messageEventId === event.id && (
              <p className="text-red-600 text-center">{message}</p>
            )}
            <h3 className="text-xl font-bold mb-2">{event.title}</h3>
            <p className="text-gray-700 mb-2">{event.description}</p>
            <p className="text-gray-700 mb-2">Date: {new Date(event.date).toLocaleDateString()}</p>
            <p className="text-gray-700 mb-2">Location: {event.location}</p>
            {event.registered ? (
              <p className="text-green-500 mb-2">Registered for this event</p>
            ) : (
              <div className="flex items-center justify-center">
                <button
                  className="bg-indigo-500 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  onClick={() => handleRegister(event.id)}
                >
                  Register
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventManagement;
