const [isLoggedIn, setIsLoggedIn] = useState(false);

const handleLogin = () => {
    setIsLoggedIn(true);
};

const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token from local storage
    setIsLoggedIn(false);
};
