import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [isLeftHovered, setIsLeftHovered] = useState(false);
  const [isRightHovered, setIsRightHovered] = useState(false);
  const navigate = useNavigate();

  const handleMouseEnter = (side) => {
    if (side === 'left') {
      setIsLeftHovered(true);
      setIsRightHovered(false);
    } else if (side === 'right') {
      setIsLeftHovered(false);
      setIsRightHovered(true);
    }
  };

  const handleMouseLeave = () => {
    setIsLeftHovered(false);
    setIsRightHovered(false);
  };

  const handleButtonClick = (page) => {
    if (page === 'signup') {
      navigate('/signup'); // Navigate to the signup page
    } else if (page === 'login') {
      navigate('/login'); // Navigate to the login page
    }
  };

  return (
    <div className="container bg-gray-900 h-screen flex relative overflow-hidden">
      <div className={`split left relative w-1/2 bg-cover bg-center transition-all duration-500 ${isLeftHovered ? 'left-expand' : 'left-shrink'}`}
        style={{ backgroundImage: "url('./image123.jpg')" }}
        onMouseEnter={() => handleMouseEnter('left')}
        onMouseLeave={handleMouseLeave}
      >
        <div className="absolute inset-0 bg-red-700 bg-opacity-70">
          <h1 className="text-white text-4xl absolute top-1/4 left-1/2 transform -translate-x-1/2">The Designer</h1>
          <button onClick={() => handleButtonClick('signup')} className="button absolute bottom-8 left-1/2 transform -translate-x-1/2 bg-transparent border-2 border-white text-white py-2.5 px-6 text-base font-bold uppercase hover:bg-red-700 hover:border-red-700">Signup</button>
        </div>
      </div>
      <div className={`split right relative w-1/2 bg-cover bg-center transition-all duration-500 ${isRightHovered ? 'right-expand' : 'right-shrink'}`}
        style={{ backgroundImage: "url('./image123.jpg')" }}
        onMouseEnter={() => handleMouseEnter('right')}
        onMouseLeave={handleMouseLeave}
      >
        <div className="absolute inset-0 bg-gray-900 bg-opacity-80">
          <h1 className="text-white text-4xl absolute top-1/4 left-1/2 transform -translate-x-1/2">The Programmer</h1>
          <button onClick={() => handleButtonClick('login')} className="button absolute bottom-8 left-1/2 transform -translate-x-1/2 bg-transparent border-2 border-white text-white py-2.5 px-6 text-base font-bold uppercase hover:bg-gray-900 hover:border-gray-900">Login</button>
        </div>
      </div>
    </div>
  );
}

export default Home;
