import React, { useEffect, useState } from 'react';
import axios from 'axios';

function EventManagement() {
const [events, setEvents] = useState([]);

useEffect(() => {
    async function fetchEvents() {
        try {
            const response = await axios.get('/events');
            setEvents(response.data);
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    }
    fetchEvents();
}, []);

const handleRegister = async (eventId) => {
  try {
      const response = await axios.post(`/events/register/${eventId}`);
      console.log(response.data.message);

      // Update events state to mark the event as registered
      const updatedEvents = events.map(event =>
          event.id === eventId ? { ...event, registered: true } : event
      );
      setEvents(updatedEvents);
  } catch (error) {
      if (error.response) {
          if (error.response.status === 404) {
              console.log('Event not found!');
              // Handle 404 error as needed (e.g., show an error message)
          } else if (error.response.status === 400) {
              console.log('Already registered for the event');
              // Handle already registered case in UI or otherwise
          } else {
              console.error('Error registering for event:', error);
              // Handle other errors (e.g., network errors)
          }
      } else {
          console.error('Error registering for event:', error);
          // Handle unexpected errors (e.g., no response from server)
      }
  }
};



return (
    <div className="flex flex-col items-center justify-center min-h-screen">
        <h2 className="text-2xl font-bold mb-4">Upcoming Events</h2>
        <div className="max-w-lg w-full">
            {events.map(event => (
                <div key={event.id} className="bg-accent1 shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <h3 className="text-xl font-bold mb-2">{event.title}</h3>
                    <p className="text-gray-700 mb-2">{event.description}</p>
                    <p className="text-gray-700 mb-2">Date: {new Date(event.date).toLocaleDateString()}</p>
                    <p className="text-gray-700 mb-2">Location: {event.location}</p>
                    {event.registered ? (
                        <p className="text-green-500 mb-2">Registered for this event</p>
                    ) : (
                        <button
                            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                            onClick={() => handleRegister(event.id)}
                        >
                            Register
                        </button>
                    )}
                </div>
            ))}
        </div>
    </div>
);
}

export default EventManagement;
