import React, { useState } from 'react'
import { useFormik } from 'formik'
import * as Yup from 'yup';
import axios from 'axios';

function Signup() {
    const [message, setMessage] = useState(null);

    const formik = useFormik({
        initialValues: {
            name:'',
            email:'',
            password:''
        },
        validationSchema: Yup.object({
            name: Yup.string().max(15, "Must be 20 Characters or less").required("Name is required"),
            email: Yup.string().email('Invalid email').required('Email is required'),
            password: Yup.string().required('Password is required')
        }),
        onSubmit: async(values) => {
            try{
                const response = await axios.post('/signup', values);
                if (response.status === 201) {
                    setMessage('User created successfully');
                    formik.resetForm();
                    setTimeout(()=>{
                        setMessage(null);
                    }, 3000)
                }
            } catch (error) {
                console.error('Error signing up:', error)
            }
        }

    });

  return (
    <div className='m-5'>
        <form className='text-blue-600' onSubmit={formik.handleSubmit}>
            <h2 className='text-xl'>Signup</h2>
            <div>
                <input 
                className='rounded-md border border-blue-300 focus:outline-none mt-6'
                type="text"
                name='name'
                placeholder='Enter your name'
                onChange={formik.handleChange}
                value={formik.values.name}
                />
                <p className='text-red-500'>{formik.touched.name && formik.errors.name ? <p>{ formik.errors.name}</p> : null}</p>
            </div>
            <div>
                <input 
                className='rounded-md border border-blue-300 focus:outline-none mt-6'
                type="email"
                name='email'
                placeholder='Enter your email'
                onChange={formik.handleChange}
                value={formik.values.email}
                />
                <p className='text-red-500'>{formik.touched.email && formik.errors.email ? <p>{ formik.errors.email}</p> : null}</p>
            </div>
            <div>
                <input
                className='rounded-md border border-blue-300 focus:outline-none mt-6' 
                type="text"
                name='password'
                placeholder='Enter your password'
                onChange={formik.handleChange}
                value={formik.values.password}
                />
                <p className='text-red-500'>{formik.touched.password && formik.errors.password ? <p>{ formik.errors.password}</p> : null}</p>
            </div>
            <div className='ml-8 mt-3'>
                <button type='submit'
                className='border border-accent1 p-3 rounded'
                >
                    Sign
                </button>
            </div>
