// EventManagement.js
import React, { useState, useEffect } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import axios from 'axios';

const EventManagement = () => {
  const [events, setEvents] = useState([]);
  const initialValues = {
    title: '',
    description: '',
    date: '',
    location: '',
    created_by: 1 // Example: Replace with dynamic admin user ID
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Fetch events error:', error);
      // Handle error (show error message, etc.)
    }
  };

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      const response = await axios.post('http://localhost:5000/events', values);
      console.log(response.data);
      // Handle success (update state, show success message, etc.)
      fetchEvents(); // Refresh events after creation
    } catch (error) {
      console.error('Create event error:', error);
      // Handle error (show error message, etc.)
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await axios.delete(`http://localhost:5000/events/${eventId}`);
      console.log(response.data);
      // Handle success (update state, show success message, etc.)
      fetchEvents(); // Refresh events after deletion
    } catch (error) {
      console.error('Delete event error:', error);
      // Handle error (show error message, etc.)
    }
  };

  return (
    <div>
      <h2>Event Management</h2>

      <h3>Create Event</h3>
      <Formik
        initialValues={initialValues}
        validate={values => {
          const errors = {};
          if (!values.title) {
            errors.title = 'Required';
          }
          if (!values.description) {
            errors.description = 'Required';
          }
          if (!values.date) {
            errors.date = 'Required';
          }
          if (!values.location) {
            errors.location = 'Required';
          }
          return errors;
        }}
        onSubmit={handleSubmit}
      >
        {({ isSubmitting }) => (
          <Form>
            <Field type="text" name="title" placeholder="Title" />
            <ErrorMessage name="title" component="div" />
            <Field type="text" name="description" placeholder="Description" />
            <ErrorMessage name="description" component="div" />
            <Field type="date" name="date" />
            <ErrorMessage name="date" component="div" />
            <Field type="text" name="location" placeholder="Location" />
            <ErrorMessage name="location" component="div" />
            <button type="submit" disabled={isSubmitting}>
              Create Event
            </button>
          </Form>
        )}
      </Formik>

      <h3>Events List</h3>
      <ul>
        {events.map(event => (
          <li key={event.id}>
            <div>Title: {event.title}</div>
            <div>Description: {event.description}</div>
            <div>Date: {event.date}</div>
            <div>Location: {event.location}</div>
            <button onClick={() => handleDeleteEvent(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventManagement;
