import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

function Signup() {
    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            password: ''
        },
        validationSchema: Yup.object({
            name: Yup.string().max(20, "Must be 20 characters or less").required("Name is required"),
            email: Yup.string().email('Invalid email').required('Email is required'),
            password: Yup.string().required('Password is required')
        }),
        onSubmit: async (values) => {
            try {
                const response = await fetch('/signup', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(values),
                });
                if (response.ok) {
                    console.log('User signed up successfully!');
                    // Optionally, redirect or show a success message
                } else {
                    console.error('Signup failed.');
                    // Handle error, show error message, etc.
                }
            } catch (error) {
                console.error('Error signing up:', error);
                // Handle network error, show error message, etc.
            }
        }
    });

    return (
        <div className='m-5'>
            <form className='text-blue-600' onSubmit={formik.handleSubmit}>
                <h2 className='text-xl'>Signup</h2>
                <div>
                    <input 
                        className='rounded-md border border-blue-300 focus:outline-none mt-6'
                        type="text"
                        name='name'
                        placeholder='Enter your name'
                        onChange={formik.handleChange}
                        value={formik.values.name}
                    />
                    {formik.touched.name && formik.errors.name ? (
                        <p className='text-red-500'>{formik.errors.name}</p>
                    ) : null}
                </div>
                <div>
                    <input 
                        className='rounded-md border border-blue-300 focus:outline-none mt-6'
                        type="email"
                        name='email'
                        placeholder='Enter your email'
                        onChange={formik.handleChange}
                        value={formik.values.email}
                    />
                    {formik.touched.email && formik.errors.email ? (
                        <p className='text-red-500'>{formik.errors.email}</p>
                    ) : null}
                </div>
                <div>
                    <input
                        className='rounded-md border border-blue-300 focus:outline-none mt-6'
                        type="password"
                        name='password'
                        placeholder='Enter your password'
                        onChange={formik.handleChange}
                        value={formik.values.password}
                    />
                    {formik.touched.password && formik.errors.password ? (
                        <p className='text-red-500'>{formik.errors.password}</p>
                    ) : null}
                </div>
                <div className='ml-8 mt-3'>
                    <button type='submit' className='border border-accent1 p-3 rounded'>
                        Sign Up
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Signup;
